import { useState, useEffect } from "react"
import NavbarApp from "./components/NavbarApp"
import BarcodeReader from 'react-barcode-reader'

import axios from "axios"

import {
  Container,
  Row,
  Col,
  InputGroup,
  FormControl,
  Button,
  Form,
  Card,
  ListGroup,
  ListGroupItem
} from "react-bootstrap"

